  /**
   * The last name
   */
  @Prop() last: string;

   /**
   * Theme
   */
  @Prop() theme: string;
  private getText(): string {
    return format(this.first, this.middle, this.last);
  }

  render() {
    return <div>Hello, World! I'm <a href="styles.css">test</a> {this.getText()}</div>;
  }
}

